mapper.select:
SELECT COUNT(*) 
  FROM EMPLOYEES 
 WHERE EMPLOYEE_ID=#{id}

SELECT COUNT(*)
  FROM JOBS 
 WHERE JOB_TITLE = #{id}

SELECT COUNT(*)
  FROM DEPARTMENTS 
 WHERE DEPARTMENT_NAME = #{id}

mapper.update:
UPDATE EMPLOYEES
   SET EMAIL = #{email}
   	 , JOB_ID = #{jobId} 
   	 , DEPARTMENT_ID = #{deptId}
   	 , FIRST_NAME = SUBSTR(#{empName}, 1,INSTR(' ')-1)
   	 , LAST_NAME = SUBSTR(#{empName}, INSTR(#{empName}, ' ') + 1, LENGTH(#{empName}))
 WHERE EMPLOYEE_ID = #{empId}
